<?phpnamespace Culpa;use Illuminate\Database\Eloquent\Model;/** * A model with custom names for fields * PHP 5.4+ */class CustomBlameableModel extends Model{    use Blameable;    protected $table = 'posts';    protected $softDelete = true;    protected $blameable = array(        'created' => 'authorId',        'updated' => 'editorId',        'deleted' => 'purgerId',    );}CustomBlameableModel::observe(new BlameableObserver);